INSERT INTO `wp_posts` VALUES (304,1,'2025-06-27 05:19:32','2025-06-26 20:19:32','<!-- wp:group {"align":"full","layout":{"type":"constrained"}} -->\n<div class="wp-block-group alignfull"><!-- wp:group {"align":"full","style":{"spacing":{"padding":{"top":"0","bottom":"var:preset|spacing|60"}}},"layout":{"type":"constrained"}} -->\n<div class="wp-block-group alignfull" style="padding-top:0;padding-bottom:var(--wp--preset--spacing--60)"><!-- wp:block {"ref":180,"content":{"sub-text":{"content":"MISSION"},"mein-text":{"content":"ミッション"}}} /-->\n\n<!-- wp:paragraph {"align":"center","style":{"spacing":{"margin":{"top":"var:preset|spacing|40"}},"typography":{"fontStyle":"normal","fontWeight":"700"}},"fontSize":"large"} -->\n<p class="has-text-align-center has-large-font-size" style="margin-top:var(--wp--preset--spacing--40);font-style:normal;font-weight:700">朝の目覚めを通じて、<br>人々のつながりと個人の成長を促進し、活力ある社会を創造する</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {"id":56,"width":"446px","sizeSlug":"full","linkDestination":"none","align":"center","style":{"spacing":{"margin":{"top":"var:preset|spacing|60"}}}} -->\n<figure class="wp-block-image aligncenter size-full is-resized" style="margin-top:var(--wp--preset--spacing--60)"><img src="https://block-editor-test.sasakishogo.comwp-content/uploads/2024/07/about-mission.png" alt="" class="wp-image-56" style="width:446px"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:group -->\n\n<!-- wp:group {"align":"full","style":{"spacing":{"padding":{"bottom":"var:preset|spacing|80"}}},"backgroundColor":"custom-gray-more-light","layout":{"type":"constrained"}} -->\n<div class="wp-block-group alignfull has-custom-gray-more-light-background-color has-background" style="padding-bottom:var(--wp--preset--spacing--80)"><!-- wp:image {"id":57,"width":"240px","sizeSlug":"full","linkDestination":"none","align":"center","style":{"spacing":{"margin":{"bottom":"var:preset|spacing|40"}}}} -->\n<figure class="wp-block-image aligncenter size-full is-resized" style="ma